(1, 'BanStatusServiceStatusBanned', 'BanStatusServiceStatusBanned', 'import React from \'react\';\nimport PropTypes from \'prop-types\';\nimport Layout from \'../layouts/Layout\';\nimport Header from \'../modules/Header\';\nimport Body from \'../modules/Body\';\nimport Footer from \'../modules/Footer\';\nimport EmptySpace from \'../modules/EmptySpace\';\nimport EmailSignature from \'./EmailSignature\';\nimport { url } from \'../../../config\';\nimport { COMMON_TEXT_COLOR } from \'../../../constants/index\';\n\nclass BanStatusServiceStatusBanned extends React.Component {\n\n    static propTypes = {\n        content: PropTypes.shape({\n            userMail: PropTypes.string.isRequired,\n            siteName: PropTypes.string.isRequired,\n        }).isRequired\n    };\n\n    render() {\n        const textStyle = {\n            color: COMMON_TEXT_COLOR,\n            backgroundColor: \'#F7F7F7\',\n            fontFamily: \'Arial\',\n            fontSize: \'16px\',\n            padding: \'35px\'\n        };\n        const { content: { userName, userMail, adminMail, logo, siteName } } = this.props;\n        let mailTo = \'mailto:\' + adminMail;\n\n        return (\n            <Layout>\n                <Header\n                    color=\"rgb(255, 90, 95)\"\n                    backgroundColor=\"#F7F7F7\"\n                    logo={logo}\n                    siteName={siteName}\n                />\n                <Body textStyle={textStyle}>\n                    <div>\n                        Dear {userName},\n                    </div>\n                    <EmptySpace height={20} />\n                    <div>\n                        Your account has been banned and you are not allowed to explore the website.\n                    </div>\n                    <EmptySpace height={20} />\n                    <div>\n                        Please get in touch with <a href={mailTo}>{adminMail}</a> if you have any questions.\n                    </div>\n                    <EmptySpace height={40} />\n                    <EmailSignature siteName={siteName} />\n                </Body>\n                <Footer siteName={siteName} />\n                <EmptySpace height={20} />\n            </Layout>\n        );\n    }\n\n}\n\nexport default BanStatusServiceStatusBanned;', '2025-06-19 22:22:18', '2025-06-19 22:22:18'),
(2, 'BanStatusServiceStatusUnBanned', 'BanStatusServiceStatusUnBanned', 'import React from \'react\';\r\nimport PropTypes from \'prop-types\';\r\nimport Layout from \'../layouts/Layout\';\r\nimport Header from \'../modules/Header\';\r\nimport Body from \'../modules/Body\';\r\nimport Footer from \'../modules/Footer\';\r\nimport EmptySpace from \'../modules/EmptySpace\';\r\nimport EmailSignature from \'./EmailSignature\';\r\nimport { url } from \'../../../config\';\r\nimport { COMMON_TEXT_COLOR } from \'../../../constants/index\';\r\n\r\nclass BanStatusServiceStatusUnBanned extends React.Component {\r\n\r\n	static propTypes = {\r\n		content: PropTypes.shape({\r\n			userMail: PropTypes.string.isRequired,\r\n			siteName: PropTypes.string.isRequired,\r\n		}).isRequired\r\n	};\r\n\r\n	render() {\r\n\r\n		const textStyle = {\r\n			color: COMMON_TEXT_COLOR,\r\n			backgroundColor: \'#F7F7F7\',\r\n			fontFamily: \'Arial\',\r\n			fontSize: \'16px\',\r\n			padding: \'35px\'\r\n		};\r\n		const { content: { userName, userMail, adminMail, logo, siteName } } = this.props;\r\n		let mailTo = \'mailto:\' + adminMail;\r\n\r\n		return (\r\n			<Layout>\r\n				<Header\r\n					color=\"rgb(255, 90, 95)\"\r\n					backgroundColor=\"#F7F7F7\"\r\n					logo={logo}\r\n					siteName={siteName}\r\n				/>\r\n				<Body textStyle={textStyle}>\r\n					<div>\r\n						Dear {userName},\r\n					</div>\r\n					<EmptySpace height={20} />\r\n					<div>\r\n						Your account has been unblocked now, you can log in and explore the website.\r\n					</div>\r\n					<EmptySpace height={20} />\r\n					<div>\r\n						Please get in touch with <a href={mailTo}>{adminMail}</a> if you have any questions.\r\n					</div>\r\n					<EmptySpace height={40} />\r\n					<EmailSignature siteName={siteName} />\r\n				</Body>\r\n				<Footer siteName={siteName} />\r\n				<EmptySpace height={20} />\r\n			</Layout>\r\n		);\r\n	}\r\n\r\n}\r\n\r\nexport default BanStatusServiceStatusUnBanned;', '2025-06-19 22:22:18', '2025-06-19 22:22:18'),
(3, 'BookingConfirmationGuest', 'BookingConfirmationGuest', 'import React, { Component } from \'react\';\r\nimport PropTypes from \'prop-types\';\r\nimport { Table, TBody, TR, TD } from \'oy-vey\';\r\nimport Layout from \'../layouts/Layout\';\r\nimport Header from \'../modules/Header\';\r\nimport Body from \'../modules/Body\';\r\nimport Footer from \'../modules/Footer\';\r\nimport EmptySpace from \'../modules/EmptySpace\';\r\nimport EmailSignature from \'./EmailSignature\';\r\nimport { url } from \'../../../config\';\r\nimport { COMMON_COLOR, COMMON_TEXT_COLOR } from \'../../../constants/index\';\r\n\r\nclass BookingConfirmationGuest extends Component {\r\n	static propTypes = {\r\n		content: PropTypes.shape({\r\n			hostName: PropTypes.string.isRequired,\r\n			guestName: PropTypes.string.isRequired,\r\n			listTitle: PropTypes.string.isRequired,\r\n			listCity: PropTypes.string.isRequired,\r\n			threadId: PropTypes.number.isRequired,\r\n			siteName: PropTypes.string.isRequired,\r\n		}).isRequired\r\n	};\r\n\r\n	render() {\r\n		const textStyle = {\r\n			color: COMMON_TEXT_COLOR,\r\n			backgroundColor: \'#F7F7F7\',\r\n			fontFamily: \'Arial\',\r\n			fontSize: \'16px\',\r\n			padding: \'35px\',\r\n		};\r\n\r\n		const linkText = {\r\n			color: COMMON_COLOR,\r\n			fontSize: \'16px\',\r\n			textDecoration: \'none\',\r\n			cursor: \'pointer\',\r\n		}\r\n\r\n\r\n		const { content: { guestName, hostName, listTitle, listCity, threadId, logo, siteName } } = this.props;\r\n		let contactURL = url + \'/message/\' + threadId + \'/renter\';\r\n\r\n		return (\r\n			<Layout>\r\n				<Header\r\n					color=\"rgb(255, 90, 95)\"\r\n					backgroundColor=\"#F7F7F7\"\r\n					logo={logo}\r\n					siteName={siteName}\r\n				/>\r\n				<div>\r\n					<Table width=\"100%\" >\r\n						<TBody>\r\n							<TR>\r\n								<TD style={textStyle}>\r\n									<EmptySpace height={20} />\r\n									<div>\r\n										Hi {guestName},\r\n									</div>\r\n									<EmptySpace height={20} />\r\n									<div>\r\n										Get ready - you are going for a trip\r\n									</div>\r\n									<EmptySpace height={20} />\r\n									<div>\r\n										{hostName} has confirmed your request. Please review the details of your trip and\r\n										{\' \'}<a style={linkText} href={contactURL}>contact owner</a>{\' \'} to coordinate the trip timings and key exchange.\r\n									</div>\r\n									<EmptySpace height={20} />\r\n									<EmailSignature siteName={siteName} />\r\n								</TD>\r\n							</TR>\r\n						</TBody>\r\n					</Table>\r\n					<EmptySpace height={40} />\r\n				</div>\r\n				<Footer siteName={siteName} />\r\n				<EmptySpace height={20} />\r\n			</Layout>\r\n		);\r\n	}\r\n}\r\n\r\nexport default BookingConfirmationGuest;\r\n', '2025-06-19 22:22:18', '2025-06-19 22:22:18'),
(4, 'BookingConfirmationHost', 'BookingConfirmationHost', 'import React from \'react\';\r\nimport PropTypes from \'prop-types\';\r\nimport moment from \'moment\';\r\nimport { Table, TBody, TR, TD } from \'oy-vey\';\r\n\r\nimport Layout from \'../layouts/Layout\';\r\nimport Header from \'../modules/Header\';\r\nimport Body from \'../modules/Body\';\r\nimport Footer from \'../modules/Footer\';\r\nimport EmptySpace from \'../modules/EmptySpace\';\r\n\r\nimport { photosShow } from \'../../../helpers/photosShow\';\r\nimport { url, profilePhotouploadDir } from \'../../../config\';\r\nimport { formatTime } from \'../../../helpers/formatting\';\r\nimport { COMMON_COLOR, COMMON_TEXT_COLOR } from \'../../../constants/index\';\r\n\r\nclass BookingConfirmationHost extends React.Component {\r\n\r\n  static propTypes = {\r\n    content: PropTypes.shape({\r\n      reservationId: PropTypes.number.isRequired,\r\n      threadId: PropTypes.number.isRequired,\r\n      confirmationCode: PropTypes.number.isRequired,\r\n      guestName: PropTypes.string.isRequired,\r\n      guestLastName: PropTypes.string.isRequired,\r\n      guestLocation: PropTypes.string.isRequired,\r\n      guestProfilePic: PropTypes.string.isRequired,\r\n      guestJoinedDate: PropTypes.string.isRequired,\r\n      checkIn: PropTypes.string.isRequired,\r\n      checkOut: PropTypes.string.isRequired,\r\n      guests: PropTypes.number.isRequired,\r\n      allowedCheckInTime: PropTypes.string.isRequired,\r\n      allowedCheckOutTime: PropTypes.string.isRequired,\r\n      siteName: PropTypes.string.isRequired,\r\n    }).isRequired\r\n  };\r\n\r\n  render() {\r\n    const textStyle = {\r\n      color: COMMON_TEXT_COLOR,\r\n      backgroundColor: \'#F7F7F7\',\r\n      fontFamily: \'Arial\',\r\n      fontSize: \'16px\',\r\n      padding: \'10px\',\r\n      textAlign: \'center\'\r\n    };\r\n\r\n    const buttonStyle = {\r\n      margin: 0,\r\n      fontFamily: \'Arial\',\r\n      padding: \'10px 16px\',\r\n      textDecorati